### Create the glexec User #glexec:x:463:463:glexec pilot user:/home/glexec:/bin/bash ### Create the gratia User #gratia:x:464:464:gratia runtime user:/etc/gratia:/sbin/nologin
/usr/sbin/groupadd -g &OSG_glexecgid; glexec swapgid=`getent group glexec | cut -d: -f3` swapgroup=`getent group &OSG_glexecgid; | cut -d: -f1` [ ! -z "$swapgroup" ]&&[ "x$swapgroup" != "xglexec" ]&&/usr/sbin/groupmod -o -g $swapgid $swapgroup /usr/sbin/groupmod -g &OSG_glexecgid; glexec
/usr/sbin/groupadd -g &OSG_gratiagid; gratia swapgid=`getent group gratia | cut -d: -f3` swapgroup=`getent group &OSG_gratiagid; | cut -d: -f1` [ ! -z "$swapgroup" ]&&[ "x$swapgroup" != "xgratia" ]&&/usr/sbin/groupmod -o -g $swapgid $swapgroup /usr/sbin/groupmod -g &OSG_gratiagid; gratia
/usr/sbin/useradd -r -u &OSG_glexecuid; -g &OSG_glexecgid; -c "gLExec user account" -s /sbin/nologin -d /etc/glexec glexec swapuid=`getent passwd glexec | cut -d: -f3` swapuser=`getent passwd &OSG_glexecuid; | cut -d: -f1` [ ! -z "$swapuser" ]&&[ "x$swapuser" != "xglexec" ]&&/usr/sbin/usermod -o -u $swapuid $swapuser /usr/sbin/usermod -u &OSG_glexecuid; -g &OSG_glexecgid; glexec
/usr/sbin/useradd -r -u &OSG_gratiauid; -g &OSG_gratiagid; -c "gratia runtime user" -s /sbin/nologin -d /etc/gratia gratia swapuid=`getent passwd gratia | cut -d: -f3` swapuser=`getent passwd &OSG_gratiauid; | cut -d: -f1` [ ! -z "$swapuser" ]&&[ "x$swapuser" != "xgratia" ]&&/usr/sbin/usermod -o -u $swapuid $swapuser /usr/sbin/usermod -u &OSG_gratiauid; &OSG_gratiagid; gratia
