kickstart.post:
touch /root/yum-install.log echo "install osg frontier squid" >> /root/yum-install.log 2>&1 ### Create the squid user #squid:x:450:450:squid management user:/home/squid:/sbin/nologin echo creating squid user >> /root/yum-install.log 2>&1 echo "/usr/sbin/groupadd -g &OSG_squidgid; squid" >> /root/yum-install.log 2>&1 echo "/usr/sbin/useradd -u &OSG_squiduid; -g &OSG_squidgid; -c \"squid management user\" -s /sbin/nologin -d /home/squid squid" >> /root/yum-install.log 2>&1 /usr/sbin/groupadd -g &OSG_squidgid; squid /usr/sbin/useradd -u &OSG_squiduid; -g &OSG_squidgid; -c "squid management user" -s /sbin/nologin -d /home/squid squid
echo "yum install frontier-squid" >> /root/yum-install.log 2>&1 yum install frontier-squid >> /root/yum-install.log 2>&1
/opt/rocks/bin/rocks add firewall host=&hostname; rulename=A20-SQUID-UDP-PORT network=public service=3401 protocol="udp" action="ACCEPT" chain="INPUT" flags="-m state --state NEW --source 128.142.0.0/16 and 188.185.0.0/17" /opt/rocks/bin/rocks report host firewall &hostname;
/opt/rocks/bin/rocks report host osg squid config &hostname;
